# Quackamollie-devtools CLI defaults
DEFAULT_SCHEMA_OUTPUT_DIR: str = "schemas"  # Default output dir of `quackamollie db generate-schema` command
DEFAULT_SCHEMA_OUTPUT_PATH: str | None = None  # Default output path of `quackamollie db generate-schema` command
DEFAULT_SHOW_MULTIPLICITY_ONE: bool = True  # Default to show the ones in the schema or not
DEFAULT_SHOW_OPERATIONS: bool = False  # Default to show operations in the schema or not
